import React, { useState } from 'react';
import { FileText, Sparkles, Edit3, Copy, Download, Loader2, CheckCircle, AlertCircle } from 'lucide-react';
import axios from 'axios';

function App() {
  const [transcript, setTranscript] = useState('');
  const [instruction, setInstruction] = useState('Summarize this transcript in bullet points for a busy executive.');
  const [summary, setSummary] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedSummary, setEditedSummary] = useState('');
  const [error, setError] = useState('');
  const [copied, setCopied] = useState(false);

  const presetInstructions = [
    'Summarize this transcript in bullet points for a busy executive.',
    'Highlight only action items and next steps.',
    'Create a detailed summary with key discussion points.',
    'Extract main decisions and outcomes.',
    'Summarize for technical team members.',
    'Create a summary suitable for stakeholders.'
  ];

  const handleGenerateSummary = async () => {
    if (!transcript.trim()) {
      setError('Please enter a transcript to summarize.');
      return;
    }

    setIsLoading(true);
    setError('');
    setSummary('');

    try {
      const response = await axios.post('/summarize', {
        transcript: transcript.trim(),
        instruction: instruction.trim()
      });

      setSummary(response.data.summary);
      setEditedSummary(response.data.summary);
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to generate summary. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleEdit = () => {
    setIsEditing(true);
    setEditedSummary(summary);
  };

  const handleSaveEdit = () => {
    setSummary(editedSummary);
    setIsEditing(false);
  };

  const handleCancelEdit = () => {
    setEditedSummary(summary);
    setIsEditing(false);
  };

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(summary);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy text:', err);
    }
  };

  const handleDownload = () => {
    const element = document.createElement('a');
    const file = new Blob([summary], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = 'meeting-summary.txt';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const handleInstructionChange = (newInstruction) => {
    setInstruction(newInstruction);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Header */}
      <header className="gradient-bg text-white py-8">
        <div className="max-w-6xl mx-auto px-4">
          <div className="text-center">
            <h1 className="text-4xl font-bold mb-2 flex items-center justify-center gap-3">
              <Sparkles className="w-10 h-10" />
              Meeting Summarizer
            </h1>
            <p className="text-xl text-blue-100">
              Transform your meeting transcripts into actionable insights with AI
            </p>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Input Section */}
          <div className="space-y-6">
            <div className="card">
              <h2 className="text-2xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                <FileText className="w-6 h-6 text-primary-600" />
                Meeting Transcript
              </h2>
              <textarea
                className="input-field h-64"
                placeholder="Paste your meeting transcript, call recording, or notes here..."
                value={transcript}
                onChange={(e) => setTranscript(e.target.value)}
              />
              <p className="text-sm text-gray-500 mt-2">
                {transcript.length} characters
              </p>
            </div>

            <div className="card">
              <h3 className="text-xl font-semibold text-gray-800 mb-4">
                Custom Instructions
              </h3>
              <textarea
                className="input-field h-24 mb-4"
                placeholder="Describe how you want the summary to be generated..."
                value={instruction}
                onChange={(e) => setInstruction(e.target.value)}
              />
              
              <div className="space-y-2">
                <p className="text-sm font-medium text-gray-700">Quick Presets:</p>
                <div className="flex flex-wrap gap-2">
                  {presetInstructions.map((preset, index) => (
                    <button
                      key={index}
                      onClick={() => handleInstructionChange(preset)}
                      className="px-3 py-1 text-sm bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-full transition-colors duration-200"
                    >
                      {preset.length > 40 ? preset.substring(0, 40) + '...' : preset}
                    </button>
                  ))}
                </div>
              </div>
            </div>

            <button
              onClick={handleGenerateSummary}
              disabled={isLoading || !transcript.trim()}
              className="btn-primary w-full disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
            >
              {isLoading ? (
                <>
                  <Loader2 className="w-5 h-5 animate-spin mr-2" />
                  Generating Summary...
                </>
              ) : (
                <>
                  <Sparkles className="w-5 h-5 mr-2" />
                  Generate Summary
                </>
              )}
            </button>

            {error && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-4 flex items-center gap-2 text-red-700">
                <AlertCircle className="w-5 h-5" />
                {error}
              </div>
            )}
          </div>

          {/* Output Section */}
          <div className="space-y-6">
            <div className="card">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-2xl font-semibold text-gray-800">
                  Generated Summary
                </h2>
                {summary && (
                  <div className="flex gap-2">
                    <button
                      onClick={handleEdit}
                      className="btn-secondary flex items-center gap-2"
                    >
                      <Edit3 className="w-4 h-4" />
                      Edit
                    </button>
                    <button
                      onClick={handleCopy}
                      className="btn-secondary flex items-center gap-2"
                    >
                      {copied ? (
                        <>
                          <CheckCircle className="w-4 h-4 text-green-600" />
                          Copied!
                        </>
                      ) : (
                        <>
                          <Copy className="w-4 h-4" />
                          Copy
                        </>
                      )}
                    </button>
                    <button
                      onClick={handleDownload}
                      className="btn-secondary flex items-center gap-2"
                    >
                      <Download className="w-4 h-4" />
                      Download
                    </button>
                  </div>
                )}
              </div>

              {!summary && !isLoading && (
                <div className="text-center py-16 text-gray-500">
                  <Sparkles className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                  <p className="text-lg">Your AI-generated summary will appear here</p>
                  <p className="text-sm">Paste a transcript and click "Generate Summary" to get started</p>
                </div>
              )}

              {isLoading && (
                <div className="text-center py-16">
                  <Loader2 className="w-16 h-16 mx-auto mb-4 text-primary-600 animate-spin" />
                  <p className="text-lg text-gray-600">AI is analyzing your transcript...</p>
                  <p className="text-sm text-gray-500">This may take a few moments</p>
                </div>
              )}

              {summary && !isLoading && (
                <div className="space-y-4">
                  {isEditing ? (
                    <div className="space-y-4">
                      <textarea
                        className="input-field h-64"
                        value={editedSummary}
                        onChange={(e) => setEditedSummary(e.target.value)}
                      />
                      <div className="flex gap-3">
                        <button
                          onClick={handleSaveEdit}
                          className="btn-primary flex-1"
                        >
                          Save Changes
                        </button>
                        <button
                          onClick={handleCancelEdit}
                          className="btn-secondary flex-1"
                        >
                          Cancel
                        </button>
                      </div>
                    </div>
                  ) : (
                    <div className="prose max-w-none">
                      <div className="bg-gray-50 rounded-lg p-4 whitespace-pre-wrap text-gray-800 leading-relaxed">
                        {summary}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 py-8 mt-16">
        <div className="max-w-6xl mx-auto px-4 text-center text-gray-600">
          <p>&copy; 2024 Meeting Summarizer. Powered by AI technology.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
